	// http://alastairc.ac/2010/03/detecting-touch-based-browsing/ 
	var el = document.createElement('div');
	el.setAttribute('ongesturestart', 'return;');
	if( typeof el.ongesturestart == "function"){
	   return true;
	} else {
	   return false
	}
